import React, { Component } from 'react'



export default class Movies extends Component {

    onAdd = (id) => {
        console.log('adding' + id)
        const theOne = this.props.movies;
        theOne.forEach( movie => {
            if(movie.id === id){
                movie.favorite = true
            }
        })
        this.setState({
            movies: theOne
        })
    }

    onRemove = (id) => {
        console.log('removing' + id)
        // const theOne = this.state.movies.favorite;
        // this.setState({
        //     favorite : theOne = false
        // })
    }

    render() {
        const movies = this.props.movies;
        const favorites = movies.filter(m => m.favorite === true);
        const others = movies.filter(m => !m.favorite);

        return (
            <div  className="DisplayMovies">
                <ul>
                {favorites.map(movie => 
                    <li>
                        {/* <img src={movies.posterUrl} alt='Movie poster'/> */}
                        <button onClick = {() => this.onRemove(movie.id) }> Remove </button>
                        <p>Title : {movie.title}</p>
                        {/* <p>Actors : {movies.actors}</p>
                        <p>Genres : {movies.genres}</p>
                        <p>Year : {movies.year}</p>
                        <p>Plot : {movies.plot}</p> */}
                    </li>)}
                </ul>
                <hr />
                <ul>
                    {others.map(movie => 
                    <li>
                        {/* <img src={movies.posterUrl} alt='Movie poster'/> */}
                        <button onClick = {() => this.onAdd(movie.id)}> Add </button>
                        <p>Title : {movie.title}</p>
                        {/* <p>Actors : {movies.actors}</p>
                        <p>Genres : {movies.genres}</p>
                        <p>Year : {movies.year}</p>
                        <p>Plot : {movies.plot}</p> */}
                    </li>)}
                </ul>
        </div>
        )
    }
}
