import React, { Component } from 'react'



export default class Movies extends Component {

    onAdd = (id) => {
        const theOne = this.state.movies;
        theOne.find( movie => {
            if(theOne.id === id){
                theOne.favorite = true
            }
        })
        this.setState({
            movies: theOne
        })
    }

    onRemove = () => {
        const theOne = this.state.movies.favorite;
        this.setState({
            favorite : theOne = false
        })
    }

    render() {
        const movies = this.props.movies;
        const favorites = movies.filter(m => m.favorite === true);
        const others = movies.filter(m => !m.favorite);

        return (
            <div  className="DisplayMovies">
                <ul>
                {favorites.map(movies => 
                    <li>
                        <img src={movies.posterUrl} alt='Movie poster'/>
                        <button onclick = {this.onRemove}> Remove </button>
                        {/* <p>Title : {movies.title}</p>
                        <p>Actors : {movies.actors}</p>
                        <p>Genres : {movies.genres}</p>
                        <p>Year : {movies.year}</p>
                        <p>Plot : {movies.plot}</p> */}
                    </li>)}
                </ul>
                <hr />
                <ul>
                    {others.map(movies => 
                    <li>
                        <img src={movies.posterUrl} alt='Movie poster'/>
                        <button onclick = {this.onAdd}> Add </button>
                        {/* <p>Title : {movies.title}</p>
                        <p>Actors : {movies.actors}</p>
                        <p>Genres : {movies.genres}</p>
                        <p>Year : {movies.year}</p>
                        <p>Plot : {movies.plot}</p> */}
                    </li>)}
                </ul>
        </div>
        )
    }
}
